publishedFunction.script:
function process_ADT(event_type, client_id, clientvisit_id, hl7_version) {



   /*

      The following Change Log Types will generate an ADT message:

   

            HL7    

      ID    Msg   Change Log Action

      --    ---   --------------------------------

      5     A04   INSERT CLIENT RECORD

      9     A08   INSERT CLIENT INSURANCE

      12    A08   INSERT CLIENT DEPENDENT

      15    A08   UPDATE CLIENT RECORD

      20    A08   UPDATE CLIENT INSURANCE

      23    A08   UPDATE CLIENT DEPENDENT

      24    A08   UPDATE CLIENTEXT RECORD

      28    A08   DELETE CLIENT INSURANCE

      42    A08   APPROVE CLIENT VISIT

      44    A08   UPDATE CLIENT VISIT ADD SIGNATURE

      89    A08   UPDATE CLIENT FROM FORM

      91    A08   UPDATE CLIENTEXT FROM FORM

      

      1002  A08   UPDATE CLIENT DEPENDENT 834     

   */



   // VERSION HISTORY:

   // 2021-07-29 MAP2   - TFS 137422 - REFACTOR

   // 2021-08-16 FP     - TFS 134781 - Added validation to check if ADT message will be generated or not.

   // 2021-08-18 MAP2   - TFS 137541 - Added Guarantor Add and Update triggers.

   // 2021-08-24 MAP2   - TFS 138779 - added example reference for g_rest_postMessageToSplunk

   // 2021-10-21 JCROWE - TFS 143908 - log message to Splunk when a message can't be generated  

   // 2022-01-11 MAP2   - ADO 10669  - Added reference for change log types 42, and 44.

   // 2022-01-13 MAP2   - ADO 8238   - Modified filter processing to incorporate new requirements.

   // 2022-03-14 MAP2   - ADO 12888  - adjusting processing to allow for sample data.

   // 2022-06-24 MAP2   - TFS 17670  - Adding code to refresh the trigger lists.

   // 2022-08-12 EK     - ADO 19744  - Now checking for is_42cfr flag on diagnoses

   // 2022-08-12 EK     - ADO 18409  - Reworked to handle multiple event_types by input rather than changelog type id lookup

   // 2022-08-17 EK     - ADO 19834  - Added logic to prevent an ADT_A06 from being generated if the clientvisit_id == 0 (PV1 is required for A06 schema)

   // 2022-08-23 EK     - ADO 19744  - diagnosis level is_42cfr flag is now ignored if the client-level consent has been set to explicitly share 42cfr data



   qie.debug('in process_ADT');



   // Set our default return value to an empty message string;



   var NO_MESSAGE_GENERATED = qie.getVariable('G_NO_MESSAGE_GENERATED');

   var msg = NO_MESSAGE_GENERATED;

   var ADT_MESSAGE_TYPE = 'ADT';



   if (!fn_is_message_type_enabled(ADT_MESSAGE_TYPE)) {

      qie.info(ADT_MESSAGE_TYPE + ' is not enabled.');

      g_rest_postMessageToSplunk(ADT_MESSAGE_TYPE + ' is not enabled., messages will not be generated', 'process_ADT');

      return NO_MESSAGE_GENERATED;

   }



   qie.debug(ADT_MESSAGE_TYPE + ' message type is enabled.');



   // If the clientvisit_Id exists but no client_Id was written to the change log table,

   // try to get the client-id from the client visit itself.



   qie.debug("Checking for client_Id " + client_id);



   if (client_id == 0 ||

      client_id == "" ||

      client_id === undefined ||

      client_id == null) {

      if (clientvisit_id !== 0 &&

         clientvisit_id !== "" &&

         clientvisit_id !== undefined &&

         clientvisit_id !== null) {

         client_id = g_da_fetch_getClientId_from_visit(clientvisit_id);

      }

   }



   // if a valid client id was not found. Do not process this change log



   if (client_id == 0) {

      qie.info("No client_id was found.  An ADT can not be processed for this request.");

      g_rest_postMessageToSplunk('No client_id was found.  An ADT can not be processed for this request.', 'process_ADT');

      return NO_MESSAGE_GENERATED;

   }



   if (g_process_filterMessage8238(client_id, clientvisit_id) == true) {

      qie.debug("FilterMessage returned TRUE.  A Message will NOT be sent.");

      g_rest_postMessageToSplunk('consent filtering stopped message processing.  An ADT can not be processed for this request.', 'process_ADT');

      return NO_MESSAGE_GENERATED;

   }

   else {

      qie.debug("FilterMessage returned FALSE.  A message CAN be sent.");

   }



   // 42 CFR diagnosis filtering is only applicable if the client consent filtering is disabled...

   // ...BECAUSE - client consent filtering defaults to NOT sending an ADT outbound, and the other option is to...

   // ...ALLOW 42cfr to go outbound, so DG1 segment filtering would never come into play if client consent filtering is enabled.

   if (g_da_fetch_is_consent_filtering_enabled() === false) {

      // Note: I did not place this logic within g_process_filterMessage8238() because it only applies to ADT, not CCDs.

      var diagnoses = da_fetch_DiagInfo_ByClientId(client_id);

      for (var i = 1; i <= diagnoses.getRowCount(); i++) {

         var is42cfrNode = diagnoses.getNode('is_42cfr', i);

         if (StringUtils.isBlank(is42cfrNode) === true) {

            continue;

         }

         var is42cfr = qie.deduceBoolean(is42cfrNode);

         if (is42cfr === true) {

            qie.info('Client Diagnosis flagged as 42 CFR, no ADT message will be created or sent as a result.');

            g_rest_postMessageToSplunk('Client Diagnosis flagged as 42 CFR, no ADT message will be created or sent as a result.', 'process_ADT');

            return NO_MESSAGE_GENERATED;

         }

      }

   }



   if (StringUtils.equalsIgnoreCase(event_type, 'A06') === true && clientvisit_id == 0) {

      qie.info('clientvisit_id cannot equal 0 for an ADT_A06 event type. The PV1 segment is required for the ADT_A06 HL7 schema. As a result, no ADT message will be generated.');

      return NO_MESSAGE_GENERATED;

   }



   if (hl7_version == '2.5') {

      qie.info('Generating ADT^' + event_type);

      msg = generate_ADT_2_5(event_type, client_id, clientvisit_id);

   }

   else {

      qie.warn('hl7_version: \'' + hl7_version + '\' is not supported by our Qvera code. As a result, no ADT message will be generated.');

   }



   if (msg == NO_MESSAGE_GENERATED) {

      qie.info('A Message could not be generated for this request.');

   }



   return msg;

}

function da_fetch_DiagInfo_ByClientId(client_id) {



   // VERSION HISTORY:

   // 2021-08-10 kbaah - TFS 137032 - REFACTORED DATABASE CONNECTION VERSION

   // 2021-08-23 FP    - TFS 138890 - Chanteg sql to use query parameterized.

   // 2021-08-26 kbaah - TFS 139130 - When an A04 or A08 message is sent it should include the DG1

   // 2021-08-27 MAP2  - TFS 138137 - Moved db validation to separate function.

   // 2021-09-02 MAP2  - TFS 139777 - Fix DG1 processing

   // 2022-03-11 EK    - ADO 12769  - SELECT now selecting specific columns, added [ClientVisit].[signature_datetime] subquery

   // 2022-03-14 MAP2  - ADO 12888  - adjusting processing to allow for sample data.

   // 2022-04-14 EK    - ADO 12769  - Added diagnosed_date to select query, icd9 diagnosis_dates are now pulled properly

   // 2022-07-25 EK    - ADO 19519  - fn_escape_reserved_hl7_characters(queryResult) added

   // 2022-08-12 EK    - ADO 19744  - Added is_42cfr to select query



   var database = channelCache.getValue('database', 'N/A');

   var NO_DATA = null;



   try {

      var sqlString = "SELECT cad.clientaxisdetail_id, cad.icd10_code, i10.icd10_short_desc, cad.axis_code, a3.icd9_desc, " +

         "cad.date_created, cad.date_resolved, cad.diagnosed_date, (" +

         "   SELECT TOP (1) signature_datetime " +

         "   FROM dbo.ClientVisit cv " +

         "   WHERE client_id = ca.client_id " +

         "       AND [status] = 'COMPLETED' " +

         "       AND clientaxisdetail_id = cad.clientaxisdetail_id " +

         "       AND (" +

         "           icd10_code = cad.icd10_code OR " +

         "           icd10_code = cad.axis_code OR " +

         "           icd10_code2 = cad.icd10_code OR " +

         "           icd10_code2 = cad.axis_code OR " +

         "           icd10_code3 = cad.icd10_code OR " +

         "           icd10_code3 = cad.axis_code OR " +

         "           icd10_code4 = cad.icd10_code OR " +

         "           icd10_code4 = cad.axis_code OR " +

         "           icd10_code5 = cad.icd10_code OR " +

         "           icd10_code5 = cad.axis_code " +

         "       ) " +

         "   ORDER BY signature_datetime DESC " +

         ") as signature_datetime, cad.updatedby_emp_id, cad.is_42cfr " +

         "FROM dbo.ClientAxis ca " +

         "INNER JOIN dbo.ClientAxisDetail cad ON cad.client_axis_id = ca.client_axis_id " +

         "LEFT OUTER JOIN dbo.Axis1 a1 ON a1.axis1_id = cad.axis_id AND cad.axislevel = 1 " +

         "LEFT OUTER JOIN dbo.Axis2 a2 ON a2.axis2_id = cad.axis_id AND cad.axislevel = 2 " +

         "LEFT OUTER JOIN dbo.Icd9cm a3 ON a3.icd9_code = cad.axis_code AND cad.axislevel = 3 " +

         "LEFT OUTER JOIN dbo.ICD10View i10 ON i10.icd10_code = cad.icd10_code " +

         "WHERE ca.active_flag = 1 AND ca.client_id = :ClientId" +

         " ORDER BY cad.axislevel, cad.axisorder ";



      var query = qie.getParameterizedQuery(sqlString);



      // Add paramaters values.



      query.setString('ClientId', client_id);



      var queryResult = query.doQuery(database, true);



      // If sample message generation is enabled, create a phi free sample record.



      if (fn_is_sample_message_enabled()) {

         queryResult = create_sample_data(queryResult);



         // TODO: Set whatever custom sample data create_sample_data does not handle         

         // queryResult.setNode("node_name",[value], 1);



      }

 

      queryResult = fn_escape_reserved_hl7_characters(queryResult);



      qie.debug("Resultset returned: " + queryResult.getNode("/"));

      qie.info("Diagnosis Found: " + queryResult.getRowCount());



      return queryResult;

   }

   catch (e) {

      qie.error("Error in da_fetch_DiagInfo_ByClientId.  STACKTRACE:  " + e);

      return NO_DATA;

   }

}
